def giturl = 'https://github.com/MNT-Lab/mntlab-dsl.git'def student = 'sivanchic'job("MNTLAB-${student}-main-build-job") {	parameters {		gitParam('BRANCH_NAME') {			type('BRANCH')		}		activeChoiceParam('TRIGGERED_JOB_NAMES') {			choiceType('CHECKBOX')			groovyScript {				script('return ["MNTLAB-sivanchic-child1-build-job", "MNTLAB-sivanchic-child2-build-job", "MNTLAB-sivanchic-child3-build-job", "MNTLAB-sivanchic-child4-build-job"]')                	}        	}	}	scm {		git(giturl, "\${BRANCH_NAME}")	}	steps {		downstreamParameterized {			trigger('$TRIGGERED_JOB_NAMES') {				block {					buildStepFailure('FAILURE')					failure('FAILURE')					unstable('UNSTABLE')				}				parameters {					predefinedProp('BRANCH_NAME', '$BRANCH_NAME')				}			}		}	}}for(i in 1..4) {	job("MNTLAB-${student}-child${i}-build-job") {		scm {			git(giturl, "\${BRANCH_NAME}" )		}		parameters {			gitParam('BRANCH_NAME') {                        	type('BRANCH')                	}		}		steps {		shell('''		mkdir -p artifacts		tar -czvf artifacts/${BRANCH_NAME//*\\/}_dsl_script.tar.gz script.sh jobs.groovy                sh script.sh > artifacts/output.txt		''')		}		publishers {			archiveArtifacts('artifacts/*')               	}	}}